private String getParentZnode() {
  String znode = conf.get(ZK_PARENT_ZNODE_KEY,
      ZK_PARENT_ZNODE_DEFAULT);
  if (!znode.endsWith("/")) {
    znode += "/";
  }
  return znode + getScopeInsideParentNode();
}
