private void scheduleRecheck(long whenNanos) {
  delayExecutor.schedule(
      new Runnable() {
        @Override
        public void run() {
          try {
            recheckElectability();
          } catch (Throwable t) {
            fatalError("Failed to recheck electability: " +
                StringUtils.stringifyException(t));
          }
        }
      },
      whenNanos, TimeUnit.NANOSECONDS);
}
