public HAServiceProtocol getHealthMonitorProxy(Configuration conf,
    int timeoutMs) throws IOException {
  InetSocketAddress addr = getHealthMonitorAddress();
  if (addr == null) {
    addr = getAddress();
  }
  return getProxyForAddress(conf, timeoutMs, addr);
}
