private void tryConnect() {
  Preconditions.checkState(proxy == null);

  try {
    synchronized (this) {
      proxy = createProxy();
    }
  } catch (IOException e) {
    LOG.warn("Could not connect to local service at " + addrToMonitor +
        ": " + e.getMessage());
    proxy = null;
    enterState(State.SERVICE_NOT_RESPONDING);
  }
}
