private <T> T zkDoWithRetries(ZKAction<T> action, Code retryCode)
    throws KeeperException, InterruptedException {
  int retry = 0;
  while (true) {
    try {
      return action.run();
    } catch (KeeperException ke) {
      if ((shouldRetry(ke.code()) || shouldRetry(ke.code(), retryCode))
          && ++retry < maxRetryNum) {
        continue;
      }
      throw ke;
    }
  }
}
