private synchronized void setZooKeeperRef(ZooKeeper zk) {
  Preconditions.checkState(this.zk == null,
      "zk already set -- must be set exactly once");
  this.zk = zk;

  for (WatchedEvent e : queuedEvents) {
    forwardEvent(e);
  }
  queuedEvents.clear();
}
