public void joinHttpServer() {
  if (httpServer != null) {
    try {
      httpServer.join();
    } catch (InterruptedException e) {
      LOG.info("Caught InterruptedException joining NameNodeHttpServer", e);
      Thread.currentThread().interrupt();
    }
  }
}
