public Object run() {
  try {
    Field f = Unsafe.class.getDeclaredField("theUnsafe");
    f.setAccessible(true);
    return f.get(null);
  } catch (NoSuchFieldException e) {
    // It doesn't matter what we throw;
    // it's swallowed in getBestComparer().
    throw new Error();
  } catch (IllegalAccessException e) {
    throw new Error();
  }
}
