void stopThreads() {
  assert dir.hasWriteLock();
  for (ZoneSubmissionTracker zst : submissions.values()) {
    zst.cancelAllTasks();
  }
  if (updaterExecutor != null) {
    updaterExecutor.shutdownNow();
  }
}
