public static void setPmdkSupportState(int stateCode) {
  for (SupportState state : SupportState.values()) {
    if (state.getStateCode() == stateCode) {
      pmdkSupportState = state;
      return;
    }
  }
  LOG.error("The state code: " + stateCode + " is unrecognized!");
}
