synchronized private void resetSubmissionTracker(final long zoneId) {
  ZoneSubmissionTracker zst = submissions.get(zoneId);
  if (zst == null) {
    zst = new ZoneSubmissionTracker();
    submissions.put(zoneId, zst);
  } else {
    zst.reset();
  }
}
