public static void mlock(ByteBuffer buffer, long len)
    throws IOException {
  assertCodeLoaded();
  if (!buffer.isDirect()) {
    throw new IOException("Cannot mlock a non-direct ByteBuffer");
  }
  mlock_native(buffer, len);
}
