public static Stat getFstat(FileDescriptor fd) throws IOException {
  Stat stat = fstat(fd);
  stat.owner = getName(IdCache.USER, stat.ownerId);
  stat.group = getName(IdCache.GROUP, stat.groupId);
  return stat;
}
