public static void renameTo(File src, File dst)
    throws IOException {
  if (!nativeLoaded) {
    if (!src.renameTo(dst)) {
      throw new IOException("renameTo(src=" + src + ", dst=" +
        dst + ") failed.");
    }
  } else {
    renameTo0(src.getAbsolutePath(), dst.getAbsolutePath());
  }
}
