public static String getLoadingFailureReason() {
  if (!NativeIO.isAvailable()) {
    return "NativeIO is not available.";
  }
  if (!SystemUtils.IS_OS_UNIX) {
    return "The OS is not UNIX.";
  }
  return null;
}
