public void writeInt(int v, int offset) throws IOException {
  Preconditions.checkState(offset + 4 <= buffer.getLength());
  byte[] b = new byte[4];
  b[0] = (byte) ((v >>> 24) & 0xFF);
  b[1] = (byte) ((v >>> 16) & 0xFF);
  b[2] = (byte) ((v >>> 8) & 0xFF);
  b[3] = (byte) ((v >>> 0) & 0xFF);
  int oldCount = buffer.setCount(offset);
  buffer.write(b);
  buffer.setCount(oldCount);
}
