static <T> int[] getNullIndexes(T[] inputs) {
  int[] nullIndexes = new int[inputs.length];
  int idx = 0;
  for (int i = 0; i < inputs.length; i++) {
    if (inputs[i] == null) {
      nullIndexes[idx++] = i;
    }
  }

  return Arrays.copyOf(nullIndexes, idx);
}
