public static void genCauchyMatrix(byte[] a, int m, int k) {
  // Identity matrix in high position
  for (int i = 0; i < k; i++) {
    a[k * i + i] = 1;
  }

  // For the rest choose 1/(i + j) | i != j
  int pos = k * k;
  for (int i = k; i < m; i++) {
    for (int j = 0; j < k; j++) {
      a[pos++] = GF256.gfInv((byte) (i ^ j));
    }
  }
}

