public static void dumpMatrix(byte[] matrix,
                              int numDataUnits, int numAllUnits) {
  for (int i = 0; i < numDataUnits; i++) {
    for (int j = 0; j < numAllUnits; j++) {
      System.out.print(" ");
      System.out.print(0xff & matrix[i * numAllUnits + j]);
    }
    System.out.println();
  }
}
