    for (int j = i + 1; j < len; j++) {
      for (int k = 0; k < dataLen; k++) {
        y[j][k] = (byte) (divTable[y[j][k] & 0x000000FF][x[j] ^
            x[j - i - 1]]);
      }
    }
    for (int j = i; j < len - 1; j++) {
      for (int k = 0; k < dataLen; k++) {
