    throws IOException {
  if (beginRecNum < 0) beginRecNum = 0;
  if (endRecNum < 0 || endRecNum > getEntryCount()) {
    endRecNum = getEntryCount();
  }
  return new Scanner(this, getLocationByRecordNum(beginRecNum),
      getLocationByRecordNum(endRecNum));
}

