private Pair searchPair(int curRetry) {
  int i = 0;
  for(; i < pairs.size() && curRetry > pairs.get(i).numRetries; i++) {
    curRetry -= pairs.get(i).numRetries;
  }
  return i == pairs.size()? null: pairs.get(i);
}
