synchronized V waitAsyncValue(long timeout, TimeUnit unit)
    throws InterruptedException, TimeoutException {
  if (value != null) {
    return value;
  }
  AsyncGet.Util.wait(this, timeout, unit);
  if (value != null) {
    return value;
  }

  throw new TimeoutException("waitCallReturn timed out "
      + timeout + " " + unit);
}
