synchronized void processRetryInfo() {
  counters.retries++;
  if (retryInfo.isFailover()) {
    retryInvocationHandler.proxyDescriptor.failover(
        retryInfo.expectedFailoverCount, method, callId);
    counters.failovers++;
  }
  retryInfo = null;
}
