public static String readString(DataInput in, int maxLength)
    throws IOException {
  int length = WritableUtils.readVIntInRange(in, 0, maxLength - 1);
  byte [] bytes = new byte[length];
  in.readFully(bytes, 0, length);
  return decode(bytes);
}
