private boolean exists(ChannelSftp channel, Path file) throws IOException {
  try {
    getFileStatus(channel, file);
    return true;
  } catch (FileNotFoundException fnfe) {
    return false;
  } catch (IOException ioe) {
    throw new IOException(E_FILE_STATUS, ioe);
  }
}
