public Path getHomeDirectory() {
  ChannelSftp channel = null;
  try {
    channel = connect();
    Path homeDir = new Path(channel.pwd());
    return homeDir;
  } catch (Exception ioe) {
    return null;
  } finally {
    try {
      disconnect(channel);
    } catch (IOException ioe) {
      return null;
    }
  }
}
