public synchronized int read() throws IOException {
  if (closed) {
    throw new IOException(E_STREAM_CLOSED);
  }

  int byteRead = wrappedStream.read();
  if (byteRead >= 0) {
    pos++;
  }
  if (stats != null & byteRead >= 0) {
    stats.incrementBytesRead(1);
  }
  return byteRead;
}
