public synchronized int read(byte[] buf, int off, int len)
    throws IOException {
  if (closed) {
    throw new IOException(E_STREAM_CLOSED);
  }

  int result = wrappedStream.read(buf, off, len);
  if (result > 0) {
    pos += result;
  }
  if (stats != null & result > 0) {
    stats.incrementBytesRead(result);
  }

  return result;
}
