private void touch(PathData item) throws IOException {
  if (!item.fs.exists(item.path)) {
    if (doNotCreate) {
      return;
    }
    item.fs.create(item.path).close();
    if (timestamp != null) {
      // update the time only if user specified a timestamp using -t option.
      updateTime(item);
    }
  } else {
    updateTime(item);
  }
}
