public static FileAttribute getAttribute(char symbol) {
  for (FileAttribute attribute : values()) {
    if (attribute.name().charAt(0) == Character.toUpperCase(symbol)) {
      return attribute;
    }
  }
  throw new NoSuchElementException("No attribute for " + symbol);
}
