protected void copyFileToTarget(PathData src, PathData target)
    throws IOException {
  // if number of thread is 1, mimic put and avoid threading overhead
  if (numThreads == 1) {
    copyFile(src, target);
    return;
  }

  Runnable task = () -> {
    try {
      copyFile(src, target);
    } catch (IOException e) {
      displayError(e);
    }
  };
  executor.submit(task);
}
