void setReadyToFlush() throws IOException {
  mapJournalsAndReportErrors(new JournalClosure() {
    @Override
    public void apply(JournalAndStream jas) throws IOException {
      if (jas.isActive()) {
        jas.getCurrentStream().setReadyToFlush();
      }
    }
  }, "setReadyToFlush");
}
