void remove(JournalManager j) {
  JournalAndStream jasToRemove = null;
  for (JournalAndStream jas: journals) {
    if (jas.getManager().equals(j)) {
      jasToRemove = jas;
      break;
    }
  }
  if (jasToRemove != null) {
    jasToRemove.abort();
    journals.remove(jasToRemove);
  }
}
