public Collection<FileStatus> getTrashRoots(boolean allUsers) {
  List<FileStatus> trashRoots = new ArrayList<>();
  for (FileSystem fs : getChildFileSystems()) {
    trashRoots.addAll(fs.getTrashRoots(allUsers));
  }
  return trashRoots;
}
