public FsStatus getStatus(Path p) throws IOException {
  if (p == null) {
    p = InodeTree.SlashPath;
  }
  InodeTree.ResolveResult<FileSystem> res = fsState.resolve(
      getUriPath(p), true);
  return res.targetFileSystem.getStatus(p);
}
