private String get(SerialNumberManager snm, int id) {
  if (tableMaskBits != 0) {
    if (id > maxEntryNumber) {
      throw new IllegalStateException(
          "serial id " + id + " > " + maxEntryNumber);
    }
    id |= snm.getMask(tableMaskBits);
  }
  return map.get(id);
}
