public void satisfyStoragePolicy(final Path path)
    throws IOException {
  final Path absF = fixRelativePart(path);
  new FSLinkResolver<Void>() {
    @Override
    public Void next(final AbstractFileSystem fs, final Path p)
        throws IOException {
      fs.satisfyStoragePolicy(path);
      return null;
    }
  }.resolve(this, absF);
}
