  // disallow construction of a Path from an empty string
  if ( path == null ) {
    throw new IllegalArgumentException(
        "Can not create a Path from a null string");
  }
  if( path.length() == 0 ) {
     throw new IllegalArgumentException(
         "Can not create a Path from an empty string");
  }   
}

