private boolean hasWindowsDrive(String path, boolean slashed) {
  if (!WINDOWS) return false;
  int start = slashed ? 1 : 0;
  return
    path.length() >= start+2 &&
    (slashed ? path.charAt(0) == '/' : true) &&
    path.charAt(start+1) == ':' &&
    ((path.charAt(start) >= 'A' && path.charAt(start) <= 'Z') ||
     (path.charAt(start) >= 'a' && path.charAt(start) <= 'z'));
}

