private static List<String> getPathComponents(String path)
    throws IOException {
  ArrayList<String> ret = new ArrayList<String>();
  for (String component : path.split(Path.SEPARATOR)) {
    if (!component.isEmpty()) {
      ret.add(component);
    }
  }
  return ret;
}
