private static void checkReturnValue(boolean rv, File p, 
                                     FsPermission permission
                                     ) throws IOException {
  if (!rv) {
    throw new IOException("Failed to set permissions of path: " + p + 
                          " to " + 
                          String.format("%04o", permission.toShort()));
  }
}
