static synchronized void reset() {
  if (INSTANCE != null) {
    INSTANCE.queue.clear();
    INSTANCE.interrupt();
    try {
      INSTANCE.join();
    } catch (InterruptedException e) {
      LOG.warn("Failed to reset renewer");
    } finally {
      INSTANCE = null;
    }
  }
}
