      quotaStr = formatSize(quota, hOption);
      quotaRem = formatSize(quota-(directoryCount+fileCount), hOption);
    }
    if (spaceQuota>0) {
      spaceQuotaStr = formatSize(spaceQuota, hOption);
      spaceQuotaRem = formatSize(spaceQuota - spaceConsumed, hOption);
