private void writeObject(ObjectOutputStream out) throws IOException {
  out.defaultWriteObject();
  out.writeInt(fd.remaining());
  if (fd.hasArray()) {
    out.write(fd.array(), fd.position(), fd.remaining());
  } else {
    byte[] x = new byte[fd.remaining()];
    fd.slice().get(x);
    out.write(x);
  }
}
