private FileStatus getNativeFileLinkStatus(final Path f,
    boolean dereference) throws IOException {
  checkPath(f);
  Stat stat = new Stat(f, getDefaultBlockSize(f), dereference, this);
  FileStatus status = stat.getFileStatus();
  return status;
}
