private void initRefeshThread (boolean runImmediately) {
  if (refreshInterval > 0) {
    refreshUsed = new Thread(new RefreshThread(this, runImmediately),
        "refreshUsed-" + dirPath);
    refreshUsed.setDaemon(true);
    refreshUsed.start();
  } else {
    running.set(false);
    refreshUsed = null;
  }
}
