private static double divide(double value, double divisor) {
  BigDecimal val = new BigDecimal(value);
  BigDecimal bDivisor = new BigDecimal(divisor);
  return val.divide(bDivisor).setScale(PRECISION, RoundingMode.HALF_UP)
      .doubleValue();
}
