void logDeprecationOnce(String name, String source) {
  DeprecatedKeyInfo keyInfo = getDeprecatedKeyInfo(name);
  if (keyInfo != null && !keyInfo.getAndSetAccessed()) {
    LOG_DEPRECATION.info(keyInfo.getWarningMessage(name, source));
  }
}
