public synchronized void unset(String name) {
  String[] names = null;
  if (!isDeprecated(name)) {
    names = getAlternativeNames(name);
    if(names == null) {
  	  names = new String[]{name};
    }
  }
  else {
    names = handleDeprecation(deprecationContext.get(), name);
  }

  for(String n: names) {
    getOverlay().remove(n);
    getProps().remove(n);
  }
}

